mapper.select:
select count(*) from tb_board where del_yn = 'N'
and notice_yn = 'N'
and board_category = #{boardType}

select count(*) from tb_board b join tb_member m on (b.reg_id = user_id) where b.del_yn = 'N'
and b.notice_yn = 'N'
and ${condition} like '%' || #{keyword} || '%' and board_category = #{boardType}

SELECT b.board_no, b.board_category, b.board_title, b.reg_id, b.count, b.notice_yn, TO_CHAR(b.mod_date, 'YYYY-MM-DD') AS mod_date, c.category_type FROM tb_board b JOIN tb_category c ON b.board_category = c.board_category WHERE b.del_yn = 'N' and b.temp_save = '02' and b.board_category = #{boardType}
and b.notice_yn = 'N'
ORDER BY b.board_no DESC

SELECT b.board_no, b.board_category, b.board_title, b.reg_id, b.count, b.notice_yn, TO_CHAR(b.mod_date, 'YYYY-MM-DD') AS mod_date, c.category_type FROM tb_board b JOIN tb_category c ON b.board_category = c.board_category join tb_member m on (b.reg_id = user_id) WHERE b.del_yn = 'N' and b.temp_save = '02' and ${condition} like '%' || #{keyword} || '%' and b.board_category = #{boardType}
and b.notice_yn = 'N'
ORDER BY b.board_no DESC

mapper.insert:
insert
	into tb_board
	(
		board_no
	, board_category
	, board_title
	, board_content
	, temp_save
	, notice_yn
	, reg_id
	, mod_id
	)
	values
	(
		seq_boardno.nextval
	, #{boardCategory}
	, #{boardTitle}
	, #{boardContent}
	, #{tempSave}
	, #{noticeYN}
	, #{regId}
	, #{modId}
	)

mapper.update:
  	update
  				tb_board
			 set
			     board_category = #{boardCategory} 
			 	 , board_title= #{boardTitle}
			   , notice_yn = #{noticeYN}
			   , board_content = #{boardContent}
			   , mod_id = #{modId}
			   , temp_save = #{tempSave}
		 where board_no = #{boardNo}

mapper.insert:
insert
