mapper.update:
		update
  				tb_category
 			set category_name = #{categoryTitle}
 			where board_category = #{boardType}

	UPDATE tb_category
      SET del_yn = 'Y'
      WHERE board_category = #{boardType}

mapper.insert:
		insert
  		into tb_reply
  		(
  			reply_no
  		, reply_content
  		, ref_no
  		, reg_id
  		, mod_id
  		)
  		values
  		(
  			seq_replyno.nextval
  		, #{replyContent}
  		, #{refNo}
  		, #{regId}
  		, #{regId}
  		)

mapper.update:
	UPDATE tb_reply
      SET del_yn = 'Y'
      WHERE reply_no = #{replyNo}

	UPDATE tb_reply
      SET reply_content= #{replyContent}
      WHERE reply_no = #{replyNo}
