mapper.update:
update tb_booking
   set bk_content = #{bkContent},
   	   mod_date = sysdate,

	       bk_start_dt = to_date(#{bkStartDate}||' '||#{bkStartTime},'YYYY/MM/DD HH24:MI'),
	   	   bk_end_dt = to_date(#{bkEndDate}||' '||#{bkEndTime},'YYYY/MM/DD HH24:MI'),

   	   assets_no = (select assets_no
   	   				  from tb_assets
   	   				 where assets_name = #{assetsName}
   	   				)
 where booking_no = #{bookingNo}

mapper.select:
select count(*)
  from tb_assets 
where del_yn = 'N'

  and main_code = #{mainName}


  and sub_code = (select sub_code
 					 from tb_common
 				    where main_code = #{mainName}
 				      and sub_name = #{subName}
 				      and del_yn = 'N')


  and assets_name like '%'||#{keyword}||'%'

mapper.update:
update tb_assets
  set del_yn = 'Y'
where assets_No = #{assetsNo}

mapper.insert:
insert
  into tb_assets
       (
        assets_no
       ,main_code
       ,sub_code
       ,assets_name
       ,enr_id
       ,mod_id
       )
 values
 	   (
 		'ASS_'|| LPAD(SEQ_ASSNO.NEXTVAL, 3, '0')
 	   ,#{ass.mainCode}
 	   ,( select sub_code
 	   		from tb_common
 	   	   where main_code = #{ass.mainCode}	
 	   	     and sub_name = #{ass.subName}
 	   	   group by sub_code
 	   	 )
 	   	,#{ass.assetsName}
 	   	,#{userId}
 	   	,#{userId}
 	   )

mapper.update:
update tb_assets
   set sub_code = (select sub_code
		 	   		from tb_common
		 	   	   where main_code = #{ass.mainCode}	
		 	   	     and sub_name = #{ass.subName}
		 	   	  group by sub_code
		 	   	   ),
		assets_name = #{ass.assetsName},
		mod_id = #{userId},
		main_code = #{ass.mainCode}
where assets_no = #{ ass.assetsNo}

update tb_booking set reg_id = #{userId}, mod_id = #{userId}, status_date = sysdate,
status = 'REJ' , rej_content = #{rejContent}
status = 'USE'
where booking_no = #{bookingNo}
