mapper.update:
update tb_booking
   set bk_start_dt = #{bkStartDate}||' '||#{bkStartTime},
   	   bk_end_dt = #{bkEndDate}||' '||#{bkEndTime},
   	   bk_content = #{bkContent},
   	   mod_date = sysdate,
   	   assets_no = (select assets_no
   	   				  from tb_assets
   	   				 where assets_name = #{assetsName}
   	   				)
 where booking_no = #{bookingNo}
