mapper.insert:
		INSERT INTO TB_EDOC
          (
            ED_NO
          , ED_FORM_CODE
          , ED_TITLE
          , ED_CONTENT
          , PRES_DATE
          , SEC_CODE
          , STATUS
          , REG_DATE
          , MOD_DATE
          , REG_ID
          , MOD_ID
          , DEL_YN
          )
          VALUES
          (
            'LI' || '-' || (SELECT SUBSTR(EF.ED_FR_NAME, 1, 2)
              FROM TB_EDOC_FORM EF
              WHERE EF.ED_FR_CODE = #{edFormCode}
              AND ROWNUM = 1) || '-' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '-' || SEQ_EDOC.NEXTVAL
          , #{edFormCode}
          , #{edTitle}
          , #{edContent}
          , #{presDate}
          , #{secCode}
          , '04'
          , SYSDATE
          , SYSDATE
          , #{regId}
          , #{modId}
          , 'N'
          )
