mapper.insert:
INSERT INTO TB_MEMBER (
     USER_ID
    , DEPT_CODE
    , USER_PWD
    , USER_NAME
    , PHONE
    , BIRTH_DATE
    , EDUCATION
    , ADDRESS
    , ZIP_CODE
    , DETAIL_ADD
    , HIRE_DATE
    , RETIRE_DATE
    , SUPER_RIGHT
    , HR_RIGHT
    , SP_RIGHT
    , BOARD_RIGHT
    , DEPT_RIGHT
    , PROFILE_URL
    , SIGN_URL
    , DAYOFF
    , SECONDARY_CODE
    , SECONDARY_USESE
    , RANK_BCODE
    , RANK_SCODE
    , REG_DATE
    , MOD_DATE
    , REG_ID
    , MOD_ID
    , DEL_YN
    , GENDER
) VALUES ( 
     #{userId}
    , #{deptCode}
    , #{userPwd}
    , #{userName}
    , #{phone}
    , TO_DATE(#{birthDate}, 'YYYY-MM-DD')
    , #{education}
    , #{address}
    , #{zipCode}
    , #{detailAdd}
    , TO_DATE(#{hireDate}, 'YYYY-MM-DD')
    , NULL
    , #{superRight}
    , #{hrRight}
    , #{spRight}
    , #{boardRight}
    , #{deptRight}
    , NULL
    , NULL
    , 12
    , 'N'
    , 'N'
    , '007-'
    , #{rankScode}
    , SYSDATE
    , SYSDATE
    , #{regId}
    , #{modId}
    , 'N'
    , #{gender}
)

mapper.select:
SELECT COUNT(*) AS count
	FROM tb_member
	WHERE user_Id = #{checkId}

mapper.update:
UPDATE TB_MEMBER 
SET
	USER_PWD = #{userPwd} 
	WHERE USER_ID = #{userId}

	  UPDATE tb_dept
    	SET 
         DEPT_TITLE = #{deptTitle}
       , MOD_DATE = SYSDATE
       , MOD_ID = #{modId}

    WHERE DEPT_CODE = #{deptCode}

	UPDATE tb_category
    SET 
		     CATEGORY_NAME = #{categoryName}
		    ,CATEGORY_TYPE = #{categoryType}
		    ,MOD_DATE = SYSDATE
		    ,MOD_ID =  #{modId}
		WHERE DEPT_CODE = #{deptCode}

	UPDATE tb_category
    SET 
		 	 DEL_YN = 'Y'
		 	,MOD_ID =  #{modId}
		WHERE DEPT_CODE = #{deptCode}

	  UPDATE tb_dept
    	SET 
     	  DEL_YN = 'Y'
     	 ,MOD_ID =  #{modId}	
    WHERE DEPT_CODE = #{deptCode}

mapper.select:
 SELECT COUNT(*) AS count
	FROM tb_dept
	WHERE dept_code = #{deptCode}
