mapper.insert:
  	insert
  		into tb_mail
  		(
  			mail_no
  		, mail_title
  		, mail_content
  		, temp_save
  		, reg_id
  		, mod_id
  		)
  		values
  		(
				seq_mailno.nextval
			, #{mailTitle}
			, #{mailContent}
			, #{tempSave}
			, #{regId}
			, #{modId}
  		)

mapper.update:
  	update
  				tb_mail
			 set
			 	 	 mail_title= #{mailTitle}
			   , mail_content = #{mailContent}
			   , mod_id = #{modId}
			   , temp_save = #{tempSave}
			   , trans_time = sysdate
		 where mail_no = #{mailNo}

mapper.insert:
  	insert
  		into re_mail
  		(
  			mail_no
  		, user_id
  		)
  		values
  		(
				#{mailNo}
			, #{emailId}
  		)
