mapper.insert:
INSERT
  INTO TB_PROJECT
  (
  	PRO_NO
  , DEPT_CODE
  , PRO_TITLE
  , CLIENT
  , ADDRESS
  , DETAIL_ADD
  , START_DATE
  , END_DATE
  , REG_ID
  , MOD_ID
  )
  VALUES
  (
  	SEQ_PROJECT.NEXTVAL
  , #{deptCode}
  , #{proTitle}
  , #{client}
  , #{address}
  , #{detailAdd}
  , #{startDate}
  , #{endDate}
  , #{regId}
  , #{modId}
  )

mapper.select:
SELECT PRO_NO , PRO_TITLE , CLIENT , DEPT_TITLE AS DEPT_NAME , ADDRESS , DETAIL_ADD , TO_CHAR(START_DATE, 'YYYY-MM-DD') AS START_DATE , TO_CHAR(END_DATE, 'YYYY-MM-DD') AS END_DATE , PRO_YN FROM TB_PROJECT P JOIN TB_DEPT USING (DEPT_CODE) WHERE P.DEL_YN = 'N'
AND START_DATE >= #{startDate}
AND END_DATE <= #{endDate}
AND PRO_TITLE LIKE '%' || #{keyword} || '%'
AND CLIENT LIKE '%' || #{keyword} || '%'
ORDER BY PRO_NO DESC

SELECT COUNT(*) FROM TB_PROJECT WHERE DEL_YN = 'N'
AND START_DATE >= #{startDate}
AND END_DATE <= #{endDate}
AND PRO_TITLE LIKE '%' || #{keyword} || '%'
AND CLIENT LIKE '%' || #{keyword} || '%'

mapper.insert:
INSERT
  INTO TB_DISPATCH
  (
  	PRO_NO
  , USER_ID
  , ROLE
  , TASK
  , START_DATE
  , END_DATE
  , DIS_YN
  , REG_ID
  , MOD_ID
  )
  VALUES
  (
  	#{proNo}
  , #{userId}
  , #{role}
  , #{task}
  , #{startDate}
  , #{endDate}
  , #{disYn}
  , #{regId}
  , #{modId}
  )

mapper.select:
	SELECT
	       COUNT(*)
	  FROM TB_DISPATCH
	 WHERE PRO_NO = #{proNo}
       AND USER_ID = #{userId}

mapper.update:
UPDATE
       TB_DISPATCH
   SET ROLE = #{role}
     , TASK = #{task}
     , START_DATE = #{startDate}
     , END_DATE = #{endDate}
     , DIS_YN = #{disYn}
     , MOD_ID = #{modId}
     , MOD_DATE = SYSDATE
 WHERE PRO_NO = #{proNo}
   AND USER_ID = #{userId}

mapper.delete:
DELETE
  FROM TB_DISPATCH
 WHERE PRO_NO = #{proNo}
   AND USER_ID = #{userId}

mapper.update:
UPDATE
  	   TB_PROJECT
   SET PRO_TITLE = #{proTitle}
     , CLIENT = #{client}
     , ADDRESS = #{address}
     , DETAIL_ADD = #{detailAdd}
     , START_DATE = #{startDate}
     , END_DATE = #{endDate}
     , MOD_ID = #{modId}
 WHERE PRO_NO = #{proNo}

mapper.select:
SELECT COUNT(*) FROM TB_DISPATCH D JOIN TB_PROJECT USING (PRO_NO) WHERE DIS_YN = 'N'
AND D.START_DATE >= #{startDate}
AND D.END_DATE <= #{endDate}
AND PRO_TITLE LIKE '%' || #{keyword} || '%'
AND CLIENT LIKE '%' || #{keyword} || '%'

SELECT D.PRO_NO , CLIENT , PRO_TITLE , USER_NAME , DEPT_TITLE , TO_CHAR(D.START_DATE, 'YYYY-MM-DD') START_DATE , TO_CHAR(D.END_DATE, 'YYYY-MM-DD') END_DATE , DIS_YN FROM TB_DISPATCH D JOIN TB_PROJECT P ON (D.PRO_NO = P.PRO_NO) JOIN TB_MEMBER M USING (USER_ID) JOIN TB_DEPT DE ON (M.DEPT_CODE = DE.DEPT_CODE) WHERE DIS_YN = 'N'
AND D.START_DATE >= #{startDate}
AND D.END_DATE <= #{endDate}
AND PRO_TITLE LIKE '%' || #{keyword} || '%'
AND CLIENT LIKE '%' || #{keyword} || '%'
ORDER BY D.PRO_NO DESC

mapper.insert:
INSERT
  INTO TB_DAILY
  (
  	DAI_NO
  , PRO_NO
  , DAI_CONTENT
  , REG_ID
  , MOD_ID
  )
  VALUES
  (
  	SEQ_DAILY.NEXTVAL
  , #{proNo}
  , #{daiContent}
  , #{regId}
  , #{modId}
  )

mapper.select:
SELECT
	   COUNT(*)
  FROM TB_DAILY
 WHERE REG_ID = #{regId}
   AND PRO_NO = #{proNo}
   AND TO_CHAR(REG_DATE, 'YYYY-MM-DD') = TO_CHAR(SYSDATE, 'YYYY-MM-DD')

mapper.update:
UPDATE
       TB_DAILY
   SET DAI_CONTENT = #{daiContent}
     , DAI_ANS = #{daiAns}
     , MOD_ID = #{modId}
     , MOD_DATE = SYSDATE
 WHERE DAI_NO = #{daiNo}

mapper.select:
SELECT DAI_NO , D.PRO_NO , DAI_CONTENT , DAI_ANS , D.REG_DATE , PRO_TITLE , USER_NAME FROM TB_DAILY D JOIN TB_PROJECT P ON (D.PRO_NO = P.PRO_NO) JOIN TB_MEMBER M ON (D.REG_ID = USER_ID) WHERE D.DEL_YN = 'N' AND D.REG_ID = #{userId}
AND D.REG_DATE >= #{startDate}
AND D.REG_DATE <= #{endDate}
AND PRO_TITLE LIKE '%' || #{keyword} || '%'
ORDER BY DAI_NO DESC

		SELECT
		 	   COUNT(*)
		  FROM TB_DAILY D
		  JOIN TB_PROJECT P USING (PRO_NO)
		 WHERE D.DEL_YN = 'N'
		   AND D.REG_ID = #{userId} 

           AND D.REG_DATE >= #{startDate}


       	   AND D.REG_DATE <= #{endDate}


    	   AND PRO_TITLE LIKE '%' || #{keyword} || '%'
